function checkboxValues(){let e=[];return document.querySelectorAll('[name="categories"]').forEach(t=>{t.checked&&e.push(t.value)}),e}function showOnlyCheckedCategories(){let e=checkboxValues();document.querySelectorAll(".to-filter").forEach(t=>{let n=t.querySelector(".description").getAttribute("data-category").split(",").map(function(e){return e.trim()});e.some(e=>n.includes(e))?t.style.display="block":t.style.display="none"})}let categoriesEventBtn=document.querySelector(".cat-event");categoriesEventBtn.addEventListener("click",function(e){e.preventDefault(),showOnlyCheckedCategories()});let resetBtn=document.querySelector(".btn.prev-step.institution-show-reset");resetBtn.addEventListener("click",function(e){e.preventDefault();document.querySelectorAll(".institution").forEach(e=>{e.style.display="none",e.classList.remove("visible")})});